// src/api/weatherApi.js
import axios from 'axios';

const API_KEY = '557b6f1c98f84f9e9930c05a0c5ea558'; // Replace with your OpenWeatherMap API key
const BASE_URL = 'https://api.openweathermap.org/data/2.5/weather?lat={lat}&lon={lon}&appid=557b6f1c98f84f9e9930c05a0c5ea558}';

export const fetchWeatherData = async (city) => {
  const response = await axios.get(`${BASE_URL}/weather`, {
    params: {
      q: city,
      units: 'metric',
      appid: API_KEY,
    },
  });
  return response.data;
};

export const fetchForecastData = async (city) => {
  const response = await axios.get(`${BASE_URL}/forecast/daily`, {
    params: {
      q: city,
      units: 'metric',
      cnt: 7,
      appid: API_KEY,
    },
  });
  return response.data.list;
};
