// components/SearchBar.js
import React, { useState } from 'react';
import { TextField, Autocomplete } from '@mui/material';
import { useDebounce } from '../hooks/useDebounce'; // Implement useDebounce hook separately

const SearchBar = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (value) => {
    setSearchTerm(value);
    // Call onSearch with debounced searchTerm
    onSearch(value);
  };

  return (
    <Autocomplete
      freeSolo
      disableClearable
      options={[]}
      renderInput={(params) => (
        <TextField
          {...params}
          label="Search City"
          margin="normal"
          variant="outlined"
          onChange={(e) => handleSearch(e.target.value)}
        />
      )}
    />
  );
};

export default SearchBar;
